import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CustomerService } from '../../services/customer.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { StorageService } from '../../../../auth/components/services/storage/storage.service';
import { NzMessageService } from 'ng-zorro-antd/message';

const DATE_FORMAT = 'MM-DD-YYYY';

@Component({
  selector: 'app-book-car',
  templateUrl: './book-car.component.html',
  styleUrls: ['./book-car.component.scss']
})
export class BookCarComponent implements OnInit {
  carId: number = 1; // Example car ID
  car: any = {
    id: 1,
    brand: 'Audi',
    name: 'A4',
    description: 'A luxurious and comfortable sedan.',
    price: 45000,
    color: 'Silver',
    transmission: 'Automatic',
    type: 'Sedan',
    year: 2022,
    processedImage: 'data:image/jpeg;base64,'
  };

  validateForm!: FormGroup;
  isSpinning: boolean = false;

  constructor(
    private service: CustomerService,
    private activeRoute: ActivatedRoute,
    private fb: FormBuilder,
    private message: NzMessageService,
    private router: Router
  ) {}

  ngOnInit() {
    this.validateForm = this.fb.group({
      toDate: [null, Validators.required],
      fromDate: [null, Validators.required]
    });

    // Comment out the following line to use default data for testing
    // this.getCarById();
  }

  bookACar(data: any) {
    this.isSpinning = true;

    let bookACarDto = {
      fromDate: Date.now(),
      toDate: Date.now() + 1000 * 60 * 60 * 24 * 7,
      userId: StorageService.getUserId(),
      carId: this.carId
    };

    this.service.bookACar(bookACarDto).subscribe(
      res => {
        this.isSpinning = false;
        this.message.success('Car booked successfully');
        this.router.navigateByUrl('/customer/dashboard');
      },
      error => {
        this.isSpinning = false;
        this.message.error('Error occurred while booking the car');
      }
    );
  }

  private getCarById() {
    this.service.getCarById(this.carId).subscribe(res => {
      this.car = res;
      this.car.processedImage = `data:image/jpeg;base64,${res.returnedImage}`;
    });
  }
}
